  fragmentPosition = vertexPosition_modelspace;
  UV = vertexUV;
  fragmentNormal = vertexNormal;
  fragmentColor = vertexColor;
}

#version 330 core

// Input vertex data, different for all executions of this shader.
layout(location = 0) in vec3 vertexPosition_modelspace;

out vec4 fragmentColor;

// Values that stay constant for the whole mesh.
uniform mat4 MVP;
uniform vec4 Color;

void main(){
  // Output position of the vertex, in clip space : MVP * position
  gl_Position = MVP * vec4(vertexPosition_modelspace,1);
  fragmentColor = Color;
}

#version 330 core

in vec3 fragmentPosition;
in vec3 fragmentNormal;
in vec2 UV;
in vec4 fragmentColor;

out vec4 color;

uniform mat4 M;
uniform float SunStrength;
uniform vec3 SunDirection;
uniform vec4 SunColor;
uniform float AmbientStrength;
uniform vec4 AmbientColor;
uniform sampler2D myTextureSampler;
uniform bool texture_used;
uniform bool apply_shading;

void main(){
  vec4 temp_c;
  if(texture_used)
    temp_c = texture(myTextureSampler, UV);
  else
    temp_c = fragmentColor;
  if (temp_c.a == 0.0)
    discard;

  float brightness;
  if(apply_shading)
  {
    // calculate brightness resulting from light coming from sun
    mat3 normalMatrix = transpose(inverse(mat3(M)));
    vec3 normal = normalize(normalMatrix * fragmentNormal);
    vec3 fragVec = vec3(M * vec4(fragmentPosition, 1));
    vec3 surfaceToLight = SunDirection;
    brightness = dot(normal, surfaceToLight); // both vectors are normalized
    brightness = clamp(brightness, 0, 1);  
  }
  else
    brightness = 1.0;

  // calculate output color
  vec4 ambient_color = AmbientColor * AmbientStrength;
  vec4 sun_color = SunColor * SunStrength;
  color = (ambient_color*(1-brightness))+(sun_color*brightness);
  color.a = 1.0;
  color = color*temp_c;
  color = clamp(color, 0.0, 1.0);
}

#version 330 core

in vec4 fragmentColor;

out vec4 color;

void main(){
  color = fragmentColor;
}

#version 330 core

in vec3 fragmentPosition;
in vec2 UV;
in vec3 fragmentNormal;
flat in vec3 textureID;
in vec3 textureWeight;

out vec4 color;

uniform mat4 M;
uniform float SunStrength;
uniform vec3 SunDirection;
uniform vec4 SunColor;
uniform float AmbientStrength;
uniform vec4 AmbientColor;
uniform sampler2DArray myTextureSampler;

void main(){
  // calculate brightness resulting from light coming from sun
  mat3 normalMatrix = transpose(inverse(mat3(M)));
  vec3 normal = normalize(normalMatrix * fragmentNormal);
  vec3 fragVec = vec3(M * vec4(fragmentPosition, 1));
  vec3 surfaceToLight = SunDirection;
  float brightness = dot(normal, surfaceToLight); // both vectors are normalized
  brightness = clamp(brightness, 0, 1);

  // calculate diffuse color based on heightmap textures
  vec4 temp_c = vec4(1.0, 1.0, 1.0, 1.0);
  vec4 col1 = texture(myTextureSampler, vec3(UV, textureID.x));
  vec4 col2 = texture(myTextureSampler, vec3(UV, textureID.y));
  vec4 col3 = texture(myTextureSampler, vec3(UV, textureID.z));
  temp_c = col1*textureWeight.x + col2*textureWeight.y + col3*textureWeight.z;
  temp_c.a = 1.0;

  // calculate output color
  vec4 ambient_color = AmbientColor * AmbientStrength;
  vec4 sun_color = SunColor * SunStrength;
  color = (ambient_color*(1-brightness))+(sun_color*brightness);
  color.a = 1.0;
  color = color*temp_c;
  color = clamp(color, 0.0, 1.0);
}

#version 330 core

// Input vertex data, different for all executions of this shader.
layout(location = 0) in vec3 vertexPosition_modelspace;
layout(location = 1) in vec3 vertexNormal;
layout(location = 2) in vec2 vertexUV;
layout(location = 3) in vec3 texID;
layout(location = 4) in vec3 texWeight;

out vec3 fragmentPosition;
out vec2 UV;
out vec3 fragmentNormal;
flat out vec3 textureID;
out vec3 textureWeight;

// Values that stay constant for the whole mesh.
uniform mat4 MVP;

void main(){
  // Output position of the vertex, in clip space : MVP * position
  gl_Position = MVP * vec4(vertexPosition_modelspace,1);
  UV = vertexUV;
  fragmentPosition = vertexPosition_modelspace;
  fragmentNormal = vertexNormal;
  textureID = texID;
  textureWeight = texWeight;
}

#version 330 core
out vec4 FragColor;

in vec2 TexCoords;

uniform sampler2D screenTexture;

void main()
{ 
    FragColor = texture(screenTexture, TexCoords);
}

#version 330 core
layout (location = 0) in vec2 aPos;
layout (location = 1) in vec2 aTexCoords;

out vec2 TexCoords;

void main()
{
    gl_Position = vec4(aPos.x, aPos.y, 0.0, 1.0); 
    TexCoords = aTexCoords;
}
