data.value:
#version 330 core

// Input vertex data, different for all executions of this shader.
layout(location = 0) in vec3 vertexPosition_modelspace;
layout(location = 1) in vec4 vertexColor;
layout(location = 2) in vec2 vertexUV;

out vec4 fragmentColor;
out vec2 UV;

// Values that stay constant for the whole mesh.
uniform mat4 MVP;

void main(){
  // Output position of the vertex, in clip space : MVP * position
  gl_Position = MVP * vec4(vertexPosition_modelspace,1);
  UV = vertexUV;
  fragmentColor = vertexColor;
}

#version 330 core

in vec4 fragmentColor;
in vec2 UV;

out vec4 color;

uniform sampler2D myTextureSampler;
uniform bool texture_used;

void main(){
  vec4 temp_c = fragmentColor;
  if(texture_used)
    temp_c *= texture(myTextureSampler, UV);
  if (temp_c.a == 0.0)
    discard;
  color = temp_c * fragmentColor;
}

#version 330 core

// Input vertex data, different for all executions of this shader.
layout(location = 0) in vec3 vertexPosition_modelspace;
layout(location = 1) in vec2 vertexUV;
layout(location = 2) in vec4 vertexBoneIndex;
layout(location = 3) in vec4 vertexBoneWeight;

out vec2 UV;

// Values that stay constant for the whole mesh.
uniform mat4 MVP;
uniform mat4 boneTransforms[20];

void main(){
  vec4 Vertex;
  vec4 newVertex;
  int index;

  Vertex = vec4(vertexPosition_modelspace, 1.0);
  newVertex = vec4(0.0, 0.0, 0.0, 1.0);

  index = int(vertexBoneIndex.x);
  if(index != -1)
    newVertex = (boneTransforms[index] * Vertex) * vertexBoneWeight.x;
  index = int(vertexBoneIndex.y);
  if(index != -1)
    newVertex = (boneTransforms[index] * Vertex) * vertexBoneWeight.y + newVertex;
  index = int(vertexBoneIndex.z);
  if(index != -1)
    newVertex = (boneTransforms[index] * Vertex) * vertexBoneWeight.z + newVertex;
  index = int(vertexBoneIndex.w);
  if(index != -1)
    newVertex = (boneTransforms[index] * Vertex) * vertexBoneWeight.w + newVertex;


  // Output position of the vertex, in clip space : MVP * position
  gl_Position =  MVP * vec4(newVertex.xyz, 1.0);
  UV = vertexUV;
}

#version 330 core

// Input vertex data, different for all executions of this shader.
layout(location = 0) in vec3 vertexPosition_modelspace;
layout(location = 1) in vec4 vertexColor;
layout(location = 2) in vec2 vertexUV;
layout(location = 3) in vec3 texID;
layout(location = 4) in vec3 texWeight;

out vec4 fragmentColor;
out vec2 UV;
flat out vec3 textureID;
out vec3 textureWeight;

// Values that stay constant for the whole mesh.
uniform mat4 MVP;

void main(){
  // Output position of the vertex, in clip space : MVP * position
  gl_Position = MVP * vec4(vertexPosition_modelspace,1);
  UV = vertexUV;
  fragmentColor = vertexColor;
  textureID = texID;
  textureWeight = texWeight;
}

#version 330 core

in vec4 fragmentColor;
in vec2 UV;
flat in vec3 textureID;
in vec3 textureWeight;

out vec4 color;

uniform sampler2DArray myTextureSampler;
uniform bool texture_used;

void main(){
  vec4 temp_c = fragmentColor;
  if(texture_used)
  {
    vec4 col1 = texture(myTextureSampler, vec3(UV, textureID.x));
    vec4 col2 = texture(myTextureSampler, vec3(UV, textureID.y));
    vec4 col3 = texture(myTextureSampler, vec3(UV, textureID.z));
    temp_c = col1*textureWeight.x + col2*textureWeight.y + col3*textureWeight.z;
    temp_c.a = 1.0;
  }
  if (temp_c.a == 0.0)
    discard;
  color = temp_c;
}

#version 330 core

in vec2 UV;

out vec4 color;

uniform sampler2D myTextureSampler;

void main(){
  vec4 temp_c = texture(myTextureSampler, UV);
  if (temp_c.a == 0.0)
    discard;
  color = temp_c;
}

#version 330 core

in vec4 fragmentColor;

out vec4 color;

void main(){
  color = fragmentColor;
}

#version 330 core

// Input vertex data, different for all executions of this shader.
layout(location = 0) in vec3 vertexPosition_modelspace;

out vec4 fragmentColor;

// Values that stay constant for the whole mesh.
uniform mat4 MVP;
uniform vec4 Color;

void main(){
  // Output position of the vertex, in clip space : MVP * position
  gl_Position = MVP * vec4(vertexPosition_modelspace,1);
  fragmentColor = Color;
}
